class AppHelper{static startServiceWorker(){if('serviceWorker' in navigator){navigator.serviceWorker.register('/sw.js').then((reg)=>{if(!navigator.serviceWorker.controller){return}
if(reg.waiting){var worker=reg.waiting;worker.postMessage({action:'skipWaiting'});return}
if(reg.installing){var worker=reg.installing;worker.addEventListener('statechange',()=>{if(worker.state=='installed'){worker.postMessage({action:'skipWaiting'})}});return}
reg.addEventListener('updatefound',()=>{var worker=reg.installing;worker.addEventListener('statechange',()=>{if(worker.state=='installed'){worker.postMessage({action:'skipWaiting'})}})})});var refreshing;navigator.serviceWorker.addEventListener('controllerchange',function(){if(refreshing)return;window.location.reload();refreshing=!0})}}}